<template>
    <div class="am-audio-item" :class="{checked:info.isChecked}" @click="clickHandler">
        <div class="aai-checkbox">
            <el-checkbox v-model="info.isChecked" @change="checkboxChangeHandler"></el-checkbox>
        </div>
        <div class="ap-box">
            <AudioPlayer :title="info.title" :url="info.preUrl"/>
        </div>
    </div>
</template>
<script>
import AudioPlayer from './components/AudioPlayer.vue';
export default {
    name:'am-audio-item',
    components:{AudioPlayer},
    props:{
        info:{
            type:Object,
            default(){
                return {};
            }
        }
    },
    data(){
        return {
            dialogVisible:false,
        }
    },
    created(){
        
    },
    methods:{
        clickHandler(){
            // this.$emit('itemclick',this.info);
            // if(this.info.type == 'video' || this.info.type == 'audio'){
            //     this.$refs.VApreview.show(this.info);
            // }
            this.$emit('selected',this.info,!this.info.isChecked);
        },
        checkboxChangeHandler(){
            // console.log('checkboxChangeHandler',value)
            this.$emit('selected',this.info,!this.info.isChecked);
        }
    }
}
</script>

<style lang="scss" scoped>
.am-audio-item{
    max-width: 800px;
    min-width: 400px;
    flex: 1;
    height: 60px;
    border-radius: 2px;
    margin: 10px;
    position: relative;
    cursor: pointer;
    overflow: hidden;
    // display: flex;
    // align-items: center;

    

    .aai-checkbox{
        width: 14px;
        height: 14px;
        top: 3px;
        left: 5px;
        transform: scale(1.3);
        position: absolute;
        z-index: 4;
    }

    .ap-box{
        flex: 1;
        height: 100%;
    }

    &.checked{
        border: 2px solid #409EFF;
    }

    &:hover{

        .aai-checkbox{
            display: block;
        }
    }
}
</style>