<template>
    <div class="type-select">
        <label class="typeradio-btn" v-for="(item,index) in tabs" :key="index">
            <input type="radio" name="ass-type-select" :disabled="item.disabled" @change="inputChangeHandler($event,item)"/>
            <span class="ass-radio-dis">
                {{item.label}}
            </span>
        </label>
        <!-- <label class="typeradio-btn">
            <input type="radio" name="ass-type-select"/>
            <span class="ass-radio-dis">
                全部
            </span>
        </label>
        <label class="typeradio-btn">
            <input type="radio" name="ass-type-select"/>
            <span class="ass-radio-dis">
                视频
            </span>
        </label>
        <label class="typeradio-btn">
            <input type="radio" name="ass-type-select"/>
            <span class="ass-radio-dis">
                音频
            </span>
        </label>
        <label class="typeradio-btn">
            <input type="radio" name="ass-type-select" disabled/>
            <span class="ass-radio-dis">
                图片
            </span>
        </label> -->
    </div>
</template>

<script>
export default {
    name:'type-select',
    props:{
        enabledType:{
            type:String,
            default:'all,video,audio,image'
        }
    },
    data (){
        return {
            tabs:[
                {
                    label:'全部',
                    valueStr:'all',
                    value:0,
                    disabled: false
                },
                {
                    label:'视频',
                    valueStr:'video',
                    value:1,
                    disabled: false
                },
                {
                    label:'音频',
                    valueStr:'audio',
                    value:2,
                    disabled: false
                },
                {
                    label:'图片',
                    valueStr:'image',
                    value:3,
                    disabled: false
                }
            ]
        }
    },
    created(){
        for(let i=0;i<this.tabs.length;i++){
            this.tabs[i].disabled = !(this.enabledType.indexOf(this.tabs[i].valueStr)>-1);
        }
    },
    mounted (){
        const inputs = this.$el.querySelectorAll('input');
        let itemTabs = null;
        for(let i=0;i<this.tabs.length;i++){
            itemTabs = this.tabs[i];
            if(!this.tabs[i].disabled){
                inputs[i].checked = true;
                break;
            }
        }
        if(itemTabs){
            this.$emit('change',itemTabs);
        }
    },
    methods:{
        inputChangeHandler(event,item){
            // console.log('event',event,item)
            this.$emit('change',item);
        }
    }
}
</script>

<style lang="scss" scoped>
.type-select{
    width: 100%;
    height: 100%;

    .typeradio-btn{
        display: inline-block;
        cursor: pointer;
        width: 80px;
        height: 30px;
        position: relative;

        input[name="ass-type-select"]{
            position: absolute;
            top: 0;
            z-index: 0;
            opacity: 0;

            &:checked +.ass-radio-dis{
                color: #409EFF;
                border-bottom: 2px solid #409EFF;
            }

            &:disabled +.ass-radio-dis{
                color: #a0cfff;
            }
        }

        .ass-radio-dis{
            color: #000;
            font-size: 14px;
            position: absolute;
            padding: 10px;
        }
    }
}
</style>